*/ namespace Module\Banner\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Detail\Detail; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use Module\Banner\Type\BannerPosition; use Module\Banner\Type\BannerType; use Module\Banner\Util\BannerUtil; class BannerController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $Jf1S7) { $Jf1S7->init('banner')->field(function ($Jf1S7) { $Jf1S7->id('id', 'ID'); $Jf1S7->select('position', '位置')->optionType(BannerPosition::class); $Jf1S7->image('image', '图片'); $Jf1S7->radio('type', '样式类型')->optionType(BannerType::class)->when(BannerType::IMAGE_TITLE_SLOGAN_LINK, function ($NXiWM) { $NXiWM->text('title', '标题'); $NXiWM->textarea('slogan', '内容描述'); $NXiWM->text('linkText', '链接文字'); $NXiWM->switch('colorReverse', '颜色反转'); })->when(BannerType::VIDEO, function ($NXiWM) { $NXiWM->video('video', '视频'); })->required(); $Jf1S7->link('link', '链接'); $Jf1S7->display('created_at', L('Created At'))->listable(false); $Jf1S7->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $XM5aD) { $XM5aD->eq('position', '位置')->select(BannerPosition::class); })->hookChanged(function (Form $oqTbu) { BannerUtil::clearCache(); })->canBatchDelete(true)->enablePagination(false)->defaultOrder(array('sort', 'asc'))->canSort(true)->title('轮播图片'); } }