*/ namespace Module\Banner\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Detail\Detail; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use Module\Banner\Type\BannerPosition; use Module\Banner\Type\BannerType; use Module\Banner\Util\BannerUtil; class BannerController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $cv5kq) { $cv5kq->init('banner')->field(function ($cv5kq) { $cv5kq->id('id', 'ID'); $cv5kq->select('position', '位置')->optionType(BannerPosition::class); $cv5kq->image('image', '图片'); $cv5kq->radio('type', '样式类型')->optionType(BannerType::class)->when(BannerType::IMAGE_TITLE_SLOGAN_LINK, function ($EKfQ3) { $EKfQ3->text('title', '标题'); $EKfQ3->textarea('slogan', '内容描述'); $EKfQ3->text('linkText', '链接文字'); $EKfQ3->switch('colorReverse', '颜色反转'); })->when(BannerType::VIDEO, function ($EKfQ3) { $EKfQ3->video('video', '视频'); })->required(); $cv5kq->link('link', '链接'); $cv5kq->display('created_at', L('Created At'))->listable(false); $cv5kq->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $qM7YH) { $qM7YH->eq('position', '位置')->select(BannerPosition::class); })->hookChanged(function (Form $Yfg4L) { BannerUtil::clearCache(); })->canBatchDelete(true)->enablePagination(false)->defaultOrder(array('sort', 'asc'))->canSort(true)->title('轮播图片'); } }