*/ namespace Module\Article\Admin\Controller; use Illuminate\Routing\Controller; use ModStart\Admin\Concern\HasAdminQuickCRUD; use ModStart\Admin\Layout\AdminCRUDBuilder; use ModStart\Field\AbstractField; use ModStart\Field\Type\FieldRenderMode; use ModStart\Form\Form; use ModStart\Grid\GridFilter; use ModStart\Support\Concern\HasFields; use ModStart\Widget\TextLink; use Module\Article\Type\ArticlePosition; use Module\Article\Util\ArticleUtil; class ArticleController extends Controller { use HasAdminQuickCRUD; protected function crud(AdminCRUDBuilder $Jf1S7) { $Jf1S7->init('article')->field(function ($Jf1S7) { $Jf1S7->id('id', 'ID'); $Jf1S7->select('position', '位置')->optionType(ArticlePosition::class); $Jf1S7->text('title', '名称')->hookRendering(function (AbstractField $gxoEj, $qlKQK, $mlRDG) { switch ($gxoEj->renderMode()) { case FieldRenderMode::GRID: case FieldRenderMode::DETAIL: return TextLink::primary(htmlspecialchars($qlKQK->title), ArticleUtil::url($qlKQK->toArray()), 'target="_blank"'); } }); $Jf1S7->text('alias', '别名')->help('默认留空，可通过链接 /article/别名 访问'); $Jf1S7->richHtml('content', '内容')->listable(false); $Jf1S7->display('created_at', L('Created At'))->listable(false); $Jf1S7->display('updated_at', L('Updated At'))->listable(false); })->gridFilter(function (GridFilter $XM5aD) { $XM5aD->eq('position', '位置')->select(ArticlePosition::class); $XM5aD->like('title', L('Title')); })->title('单页文章')->hookSaved(function (Form $oqTbu) { ArticleUtil::clearCache(); }); } }